import React, { Component } from "react";
import { connect } from "react-redux";
import "../styles/profile.css";

class Profile extends Component {
  render() {
    return (
      // profile icon, takes you to profile/account where you will be able to edit stored data
      <>
        <div className="account-heading">
          <h1>Account</h1>
        </div>
        <div className="account-items">
          <div className="account-item name">
            <div>
              <p>Baby's name</p>
              <p>{this.props.babyName}</p>
            </div>
            <p
              className="edit"
              onClick={() => {
                this.props.dispatch({
                  type: "SET_SCREEN_MODE",
                  screenMode: 2,
                });
              }}
            >
              EDIT
            </p>
          </div>
          <div className="account-item dob">
            <div>
              <p>Baby's DOB</p>
              <p>{this.props.dOB}</p>
            </div>
            <p className="edit">EDIT</p>
          </div>
        </div>
        <div className="account-item logout">
          {/* log out is temporarily here for local storage clearing purposes */}
          <p
            className="log-out"
            onClick={() => {
              localStorage.clear();
              this.props.dispatch({
                type: "SET_SCREEN_MODE",
                screenMode: 0,
              });
            }}
          >
            {" "}
            Log out
          </p>
        </div>
        <p
          className="account-exit"
          onClick={() => {
            this.props.dispatch({
              type: "SET_SCREEN_MODE",
              screenMode: 2,
            });
          }}
        >
          X
        </p>
      </>
    );
  }
}

function mapStateToProps(state) {
  return {
    babyName: state.babyName,
    dOB: state.dOB,
  };
}
export default connect(mapStateToProps)(Profile);
