import React, { Component } from "react";
import "../styles/timeline.css";
import TimelineDescriptionDetails90 from "./timelineDescriptionDetails90";
import TimelineDescriptionDetails180 from "./timelineDescriptionDetails180";

class TimelineDescriptionRowCard extends Component {
  showTimelineDetails = () => {
    {
      this.props.timelineDescriptionRowCard.cardClass === "card upto90" && (
        <TimelineDescriptionDetails90 />
      );
    }
    {
      this.props.timelineDescriptionRowCard.cardClass === "card upto180" && (
        <TimelineDescriptionDetails180 />
      );
    }
  };

  render() {
    const { cardClass, title, description } =
      this.props.timelineDescriptionRowCard;
    return (
      <div onClick={this.showTimelineDetails} className={cardClass}>
        <h2>{title}</h2>
        <p>{description}</p>
      </div>
    );
  }
}

export default TimelineDescriptionRowCard;
