import React, { Component } from "react";
import { connect } from "react-redux";
import BackArrow from "../images/icons/backArrow.svg";

class EditBabyName extends Component {
  render() {
    return (
      <>
        <div className="editBabyName-heading">
          <div>
            <img src={BackArrow} alt="back arrow"></img>
          </div>
          <div>
            <h1>Edit baby name</h1>
          </div>
        </div>
        <div className="editBabyName-input">
          <input
            type="text"
            id="babyName"
            className="babyName"
            placeholder={this.props.babyName}
          />
        </div>
        <div className="editBabyName-buttons">
          <div>
            <button>SAVE</button>
          </div>
          <div>
            <button>CANCEL</button>
          </div>
        </div>
      </>
    );
  }
}

function mapStateToProps(state) {
  return {
    babyName: state.babyName,
  };
}
export default connect(mapStateToProps)(EditBabyName);
