import React, { Component } from "react";
import "../styles/passwordReset.css";
import { connect } from "react-redux";
import axios from "axios";

class PasswordReset extends Component {
  state = {};

  // dynamic user input
  onUserInput = (e) => {
    this.setState({ [e.target.id]: e.target.value }, () =>
      console.log(this.state)
    );
  };

  onResetPassword = async () => {
    const result = await axios.get(
      `http://localhost:6002/password_reset/${this.state.email}`
    );
    console.log(result);
  };

  render() {
    return (
      <div onChange={this.onUserInput} className="password-reset-page">
        <p>Find Your Account</p>
        <p>Please enter your email address to reset your password.</p>
        <input type="text" id="email"></input>
        <button onClick={this.onResetPassword}>Reset password</button>
        <button
          onClick={() => {
            this.props.dispatch({
              type: "SET_ACCOUNT_MODE",
              accountMode: 0,
            });
            this.props.dispatch({
              type: "SET_SCREEN_MODE",
              screenMode: 1,
            });
          }}
        >
          Return to login
        </button>
      </div>
    );
  }
}

export default connect()(PasswordReset);
