import React, { Component } from "react";
import { connect } from "react-redux";
import "../styles/glossary.css";

class Glossary extends Component {
  render() {
    return (
      <>
        <div className="glossary-page">
          <div className="glossary-heading">
            <h1>Glossay</h1>
            <p>For baby, hand and toy illustrations</p>
          </div>
          <div className="glossary-categories">
            <div>
              <h2>0-3 Months</h2>
            </div>
            <div>
              <h2>4-6 Months</h2>
            </div>
            <div>
              <h2>7-9 Months</h2>
            </div>
            <div>
              <h2>10-12 Months</h2>
            </div>
            <div>
              <h2>13-15 Months</h2>
            </div>
            <div>
              <h2>16-18 Months</h2>
            </div>
            <div>
              <h2>19-21 Months</h2>
            </div>
            <div>
              <h2>22-24 Months</h2>
            </div>
          </div>
          <div className="glossary-exit">
            <p
              onClick={() => {
                this.props.dispatch({
                  type: "SET_SCREEN_MODE",
                  screenMode: 2, //timeline
                });
              }}
            >
              X
            </p>
          </div>
        </div>
      </>
    );
  }
}

function mapStateToProps(state) {
  return {
    screenMode: state.screenMode,

    // this is where what I want from the store
    // will go
  };
}

export default connect(mapStateToProps)(Glossary);
