import React, { Component } from "react";
import { connect } from "react-redux";
import BackArrow from "../images/icons/backArrow.svg";

class EditBabyName extends Component {
  onUserInput = (e) => {
    this.setState({ [e.target.id]: e.target.value }, () =>
      console.log(this.state)
    );
  };

  onSave = () => {
    this.props.dispatch({
      type: "UPDATE_ACCOUNT_BABYNAME",
      babyName: this.state.babyName,
    });
  };

  render() {
    return (
      <>
        <div className="editBabyName-heading">
          <div>
            <img src={BackArrow} alt="back arrow"></img>
          </div>
          <div>
            <h1>Edit baby name</h1>
          </div>
        </div>
        <div onChange={this.onUserInput} className="editBabyName-input">
          <input
            type="text"
            id="babyName"
            className="babyName"
            placeholder={this.props.babyName}
          />
        </div>
        <div className="editBabyName-buttons">
          <div>
            <button onClick={this.onSave}>SAVE</button>
          </div>
          <div>
            <button
              onClick={() => {
                this.props.dispatch({
                  type: "SET_SCREEN_MODE",
                  screenMode: 3,
                });
              }}
            >
              CANCEL
            </button>
          </div>
        </div>
      </>
    );
  }
}

function mapStateToProps(state) {
  return {
    babyName: state.babyName,
  };
}
export default connect(mapStateToProps)(EditBabyName);
